import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";


export default function Weather() {
  const [city, setCity] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [hasError, setHasError] = useState(false);
  const [input, setInput] = useState("");

  const getWeather = (cityName) => {
    const API_KEY = "2c15e16ab21e2ac37c7a627756f01940";
    const END_POINT = `http://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`;
    fetch(END_POINT)
      .then((res) => res.json())
      .then((response) => {
        console.log(response);
        const {
          name,
          sys: { country },
          main: { temp_max, temp_min },
          weather: [{ main, description }],
          coord: { lat, lon },
        } = response;
        setData([...data,{ description : {name,
          country,
          main,
          description,
          lat,
          lon,
          temp_max,
          temp_min},id:Date.now()

        }]);
        console.log(response.main);
        console.log("description",data);

        const sorted = data.sort((a, b) => {
          return a.id - b.id;
        });

        setData(sorted)
        setLoading(true);
      })
      .catch((err) => {
        setHasError(true);
      });
  };

  // function handleChange(event) {
  //   setCity({
  //     ...city,
  //     [event.target.name]: event.target.value,
  //   });
  // }

  // function handleSubmit(event) {

  //   event.preventDefault();
  //   getWeather(city.name);
  //   event.target.reset();
  // }

  const addTodos = () => {
    console.log("aaaaa");
    console.log(input);
    getWeather(input);
    console.log("addddd", data)
    setInput("");
  };
  return (
    // <div className="container">
    //   <h1>Weather</h1>
    //   <form onSubmit={handleSubmit}>
    //     <label>
    //       <input
    //         type="text"
    //         name="name"
    //         onChange={handleChange}
    //         placeholder="Search City"
    //       />
    //     </label>
    //     <input type="submit" value="Submit" />
    //   </form>
    //   {loading && !hasError && <CityWeather data={data} />}
    //   <div style={{color: "red"}}>{hasError && <p>Something get wrong</p>}</div>
    // </div>

    <div>
      <form noValidate autoComplete="off">
        <TextField
          id="outlined-basic"
          label="Outlined"
          variant="outlined"
          value={input}
          onChange={(event) => {
            setInput(event.target.value);
          }}
        />
        <Button variant="contained" color="secondary" onClick={addTodos}>
          Secondary
        </Button>
      </form>

      {data.map((todo) => (
          <Card  key={todo.id}>
          <CardContent className="container">
          <Typography variant="h5" component="h2">{todo.description.name}, {todo.description.country} </Typography>
          <Typography color="textSecondary">{todo.description.main}</Typography>
            <Typography variant="body2" component="p">
              {todo.description.description}
              <br />
              <br />
              <span>min temp: </span> {todo.description.temp_min}
              <br/>
              <span>max temp: </span> {todo.description.temp_max}
            </Typography>
          </CardContent>
        </Card>)
      )}
    </div>
  );
}

function CityWeather({ data }) {
  console.log(data);
  return (
    <div className="cityCard">
      <h2>
        {data.name}, {data.country}
      </h2>
      <div>
        <h3> {data.main}</h3>
        <span>{data.description}</span>
      </div>
      <div>
        <p>min temp : {data.temp_min}</p>
        <p>max temp : {data.temp_max}</p>
        <p>
          location : {data.lat} {data.lon}
        </p>
      </div>
    </div>
  );
}
