import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import HighlightOffIcon from "@material-ui/icons/HighlightOff";

export default function Weather() {
  const [city, setCity] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [hasError, setHasError] = useState(false);
  const [input, setInput] = useState("");

  const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;
  const getWeather = (cityName) => {
    const END_POINT = `http://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`;
    fetch(END_POINT)
      .then((res) => res.json())
      .then((response) => {
        console.log(response);
        const {
          name,
          sys: { country },
          main: { temp_max, temp_min },
          weather: [{ main, description }],
          coord: { lat, lon },
        } = response;
        setData([
          ...data,
          {
            description: {
              name,
              country,
              main,
              description,
              lat,
              lon,
              temp_max,
              temp_min,
            },
            id: Date.now(),
          },
        ]);
        setLoading(true);
      })
      .catch((err) => {
        setHasError(true);
      });
  };
  const addTodos = () => {
    getWeather(input);
    setInput("");
  };
  return (
    <div>
      <form noValidate autoComplete="off">
        <TextField
          className="input"
          id="outlined-basic"
          label="City"
          variant="outlined"
          value={input}
          onChange={(event) => {
            setInput(event.target.value);
          }}
        />
        <Button variant="contained" color="secondary" onClick={addTodos}>
          Search
        </Button>
      </form>
      {data.map((todo) => (
        <Card className="container" key={todo.id}>
          <CardContent>
            <Typography variant="h5" component="h2">
              {todo.description.name}, {todo.description.country}{" "}
            </Typography>

            <Typography color="textSecondary">
              {todo.description.main}
            </Typography>

            <Typography variant="body2" component="p">
              {todo.description.description}
              <br />
              <br />
              <span>min temp: </span> {todo.description.temp_min}
              <br />
              <span>max temp: </span> {todo.description.temp_max}
            </Typography>
          </CardContent>
          <HighlightOffIcon
            color="secondary"
            style={{ fontSize: 40 }}
            onClick={() => {
              const selectedId = todo.id;
              console.log(selectedId);
              const remainingArray = data.filter((ori) => ori.id != selectedId);
              setData(remainingArray);
            }}
          />
        </Card>
      ))}
    </div>
  );
}

function CityWeather({ data }) {
  console.log(data);
  return (
    <div className="cityCard">
      <h2>
        {data.name}, {data.country}
      </h2>
      <div>
        <h3> {data.main}</h3>
        <span>{data.description}</span>
      </div>
      <div>
        <p>min temp : {data.temp_min}</p>
        <p>max temp : {data.temp_max}</p>
        <p>
          location : {data.lat} {data.lon}
        </p>
      </div>
    </div>
  );
}
