import React, { useState } from "react";
import "./App.css";
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
// You need to run this command
// REACT_APP_OPENWEATHERMAP_API_KEY=2c15e16ab21e2ac37c7a627756f01940 npm start
const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;
export default function Weather() {
  const [city, setCity] = useState("");
  const [data, setData] = useState();
  const [loading, setLoading] = useState(false);
  const [hasError, setHasError] = useState(false);

  const getWeather = (cityName) => {
    console.log(process.env.REACT_APP_OPENWEATHERMAP_API_KEY);
    const END_POINT = `http://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`;
    fetch(END_POINT)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        const {
          name,
          sys: { country },
          main: { temp_max, temp_min },
          weather: [{ main, description }],
          coord: { lat, lon },
        } = data;
        setData({
          name,
          country,
          main,
          description,
          lat,
          lon,
          temp_max,
          temp_min,
        });
        setLoading(true);
      })
      .catch((err) => {
        setHasError(true);
      });
  };

  function handleChange(event) {
    setCity({
      ...city,
      [event.target.name]: event.target.value,
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    getWeather(city.name);
    event.target.reset();
  }

  return (
