import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";

const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;
export default function Weather() {
  const [city, setCity] = useState("");
  const [data, setData] = useState();
  const [loading, setLoading] = useState(false);
  const [hasError, setHasError] = useState(false);
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);

  const getWeather = (cityName) => {
    const API_KEY = "2c15e16ab21e2ac37c7a627756f01940";
    const END_POINT = `http://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`;
    fetch(END_POINT)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        const {
          name,
          sys: { country },
          main: { temp_max, temp_min },
          weather: [{ main, description }],
          coord: { lat, lon },
        } = data;
        setData({
          name,
          country,
          main,
          description,
          lat,
          lon,
          temp_max,
          temp_min,
        });
        setLoading(true);
      })
      .catch((err) => {
        setHasError(true);
      });
  };

  // function handleChange(event) {
  //   setCity({
  //     ...city,
  //     [event.target.name]: event.target.value,
  //   });
  // }

  // function handleSubmit(event) {

  //   event.preventDefault();
  //   getWeather(city.name);
  //   event.target.reset();
  // }

  const addTodos = () => {
    console.log(...todos);
    setTodos([...todos, { description: input, id: 1 }]);
    getWeather(input);
    setInput("");
  };
  return (
    // <div className="container">
    //   <h1>Weather</h1>
    //   <form onSubmit={handleSubmit}>
    //     <label>
    //       <input
    //         type="text"
    //         name="name"
    //         onChange={handleChange}
    //         placeholder="Search City"
    //       />
    //     </label>
    //     <input type="submit" value="Submit" />
    //   </form>
    //   {loading && !hasError && <CityWeather data={data} />}
    //   <div style={{color: "red"}}>{hasError && <p>Something get wrong</p>}</div>
    // </div>

    <div>
      <form noValidate autoComplete="off">
        <TextField
          id="outlined-basic"
          label="Outlined"
          variant="outlined"
          value={input}
          onChange={(event) => {
            setInput(event.target.value);
          }}
        />
        <Button variant="contained" color="secondary" onClick={addTodos}>
          Secondary
        </Button>
      </form>

      <Card>
        {todos.map((todo) => {
          <CardContent class="container">
            <Typography color="textSecondary" gutterBottom>
              Word of the Day
            </Typography>
            <Typography variant="h5" component="h2"></Typography>
            <Typography color="textSecondary">adjective</Typography>
            <Typography variant="body2" component="p">
              well meaning and kindly.
              <br />
              {'"a benevolent smile"'}
            </Typography>
          </CardContent>;
        })}
      </Card>
    </div>
  );
}

function CityWeather({ data }) {
  console.log(data);
  return (
    <div className="cityCard">
      <h2>
        {data.name}, {data.country}
      </h2>
      <div>
        <h3> {data.main}</h3>
        <span>{data.description}</span>
      </div>
      <div>
        <p>min temp : {data.temp_min}</p>
        <p>max temp : {data.temp_max}</p>
        <p>
          location : {data.lat} {data.lon}
        </p>
      </div>
    </div>
  );
}
