import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import HighlightOffIcon from "@material-ui/icons/HighlightOff";

export default function Weather() {
  const [cities, setCities] = useState([]);
  const [loading, setLoading] = useState(false);
  const [hasError, setHasError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [city, setCity] = useState("");

  const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;
  const getWeather = (cityName) => {
    const END_POINT = `http://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`;
    fetch(END_POINT)
      .then((resp) => {
        resp.json() ;

    })
      .then((response) => {
        console.log(response);
        // const {
        //   name,
        //   sys: { country },
        //   main: { temp_max, temp_min },
        //   weather: [{ main, description }],
        //   coord: { lat, lon },
        // } = response;
        // setCities([
        //   ...cities,
        //   {
        //     description: {
        //       name,
        //       country,
        //       main,
        //       description,
        //       lat,
        //       lon,
        //       temp_max,
        //       temp_min,
        //     },
        //     id: Date.now(),
        //   },
        // ]);
        setLoading(true);
      })
      .catch((err) => {
        console.log("errrrr",err);
        setHasError(true);
        setErrorMessage(err);
      });
  };
  const searchCityWeather = () => {
    getWeather(city);
    setCity("");
  };
  return (
    <div>
      <form noValidate autoComplete="off">
        <TextField
          className="input"
          id="outlined-basic"
          label="City"
          variant="outlined"
          value={city}
          onChange={(event) => {
            setCity(event.target.value);
          }}
        />
        <Button variant="contained" color="secondary" onClick={searchCityWeather}>
          Search
        </Button>
      </form>

      {hasError && <Card> <CardContent>  <Typography variant="h5" component="h2" color="secondary">
              ERROR
            </Typography> 
            <Typography variant="h5" color="secondary">
            Please be sure to enter a meaningful city name!
            </Typography></CardContent></Card>}

      {cities.map((city) => (
        <Card className="container" key={city.id}>
          <CardContent>
            <Typography variant="h5" component="h2">
              {city.description.name}, {city.description.country}{" "}
            </Typography>

            <Typography color="textSecondary">
              {city.description.main}
            </Typography>

            <Typography variant="body2" component="p">
              {city.description.description}
              <br />
              <br />
              <span>min temp: </span> {city.description.temp_min}
              <br />
              <span>max temp: </span> {city.description.temp_max}
            </Typography>
          </CardContent>
          <HighlightOffIcon
            color="secondary"
            style={{ fontSize: 40 }}
            onClick={() => {
              const selectedId = city.id;
              console.log(selectedId);
              const remainingCities = cities.filter((item) => item.id != selectedId);
              setCities(remainingCities);
            }}
          />
        </Card>
      ))}
    </div>
  );
}

function CityWeather({ data }) {
  console.log(data);
  return (
    <div className="cityCard">
      <h2>
        {data.name}, {data.country}
      </h2>
      <div>
        <h3> {data.main}</h3>
        <span>{data.description}</span>
      </div>
      <div>
        <p>min temp : {data.temp_min}</p>
        <p>max temp : {data.temp_max}</p>
        <p>
          location : {data.lat} {data.lon}
        </p>
      </div>
    </div>
  );
}
